mapper.select:
select

from produce


        and produce_key=#{produceKey,jdbcType=VARCHAR}


        and produce_name=#{produceName,jdbcType=VARCHAR}


        and produce_num=#{produceNum,jdbcType=VARCHAR}


        and produce_type=#{produceType,jdbcType=VARCHAR}


        and produce_date=#{produceDate,jdbcType=TIMESTAMP}


        and produce_address=#{produceAddress,jdbcType=VARCHAR}


        and produce_cycle=#{produceCycle,jdbcType=VARCHAR}


        and produce_person=#{producePerson,jdbcType=VARCHAR}


        and produce_fresh=#{produceFresh,jdbcType=VARCHAR}


        and produce_c=#{produceC,jdbcType=VARCHAR}


        and produce_humidity=#{produceHumidity,jdbcType=VARCHAR}


        and produce_supplier_name=#{produceSupplierName,jdbcType=VARCHAR}


        and produce_sub_name=#{produceSubName,jdbcType=VARCHAR}


        and produce_user_id=#{produceUserId,jdbcType=BIGINT}
