mapper.insert:

insert into sale (sale_key, sale_name, sale_on_date, 
  sale_fresh, sale_c, sale_humidity, 
  sale_person, sale_price, user_id
  )
values (#{saleKey,jdbcType=VARCHAR}, #{saleName,jdbcType=VARCHAR}, #{saleOnDate,jdbcType=TIMESTAMP}, 
  #{saleFresh,jdbcType=VARCHAR}, #{saleC,jdbcType=VARCHAR}, #{saleHumidity,jdbcType=VARCHAR}, 
  #{salePerson,jdbcType=VARCHAR}, #{salePrice,jdbcType=VARCHAR}, #{userId,jdbcType=BIGINT}
  )

mapper.select:
select b.store_key  superKey,

from sale a
right  join `store`  b on a.sale_key = b.store_key


    and sale_key=#{saleKey,jdbcType=VARCHAR}


    and sale_name=#{saleName,jdbcType=VARCHAR}


    and sale_on_date=#{saleOnDate,jdbcType=TIMESTAMP}


    and sale_fresh=#{saleFresh,jdbcType=VARCHAR}


    and sale_c=#{saleC,jdbcType=VARCHAR}


    and sale_humidity=#{saleHumidity,jdbcType=VARCHAR}


    and sale_person=#{salePerson,jdbcType=VARCHAR}


    and sale_price=#{salePrice,jdbcType=VARCHAR}


    and ( user_id=#{userId,jdbcType=BIGINT} or user_id is null )
